import React from 'react';
import './ServiceTiles.css';

const services = [
    { name: 'کودکان من', icon: '👶' }, { name: 'نمودار رشد', icon: '📈' },
    { name: 'واکسیناسیون', icon: '💉' }, { name: 'مشاوره با متخصص', icon: '👨‍⚕️' },
    { name: 'مشاوره روانشناسی', icon: '🧠' }, { name: 'آزمایش در محل', icon: '🔬' },
    { name: 'فروشگاه', icon: '🛒' }, { name: 'سرگرمی', icon: '🎮' },
];

const ServiceTiles = () => (
    <div className="tiles-container">
        {services.map(s => (
            <div key={s.name} className="tile">
                <div className="tile-icon">{s.icon}</div>
                <div className="tile-name">{s.name}</div>
            </div>
        ))}
    </div>
);

export default ServiceTiles;
