import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './AddChildPage.css';

const AddChildPage = () => {
    const history = useHistory();
    const [formData, setFormData] = useState({ firstName: '', lastName: '', birthDate: '' });
    const [preview, setPreview] = useState(null);

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        if (name === "avatar") {
            setPreview(URL.createObjectURL(files[0]));
        } else {
            setFormData(prevState => ({ ...prevState, [name]: value }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/children', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });
            if (response.ok) {
                alert('کودک با موفقیت اضافه شد!');
                history.push('/my-children');
            } else {
                alert('خطا در افزودن کودک.');
            }
        } catch (error) {
            alert('خطا در ارتباط با سرور.');
        }
    };

    // ... JSX for the form remains the same ...
    return (
        <div className="add-child-page"> 
            {/* ... nav bar ... */}
            <div className="add-child-form-container">
                 <form onSubmit={handleSubmit} className="add-child-form">
                    {/* ... all form fields including avatar ... */}
                 </form>
            </div>
        </div>
    );
};

export default AddChildPage;
