import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
    const history = useHistory();
    const [loginInput, setLoginInput] = useState('');
    const [passwordInput, setPasswordInput] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [loginMessage, setLoginMessage] = useState('');
    const [isChatOpen, setChatOpen] = useState(false);
    const [chatInput, setChatInput] = useState('');
    const [chatMessages, setChatMessages] = useState([
        { from: 'bot', text: 'سلام! چطور می‌تونم کمک‌تون کنم؟' }
    ]);

    useEffect(() => {
        const savedLogin = localStorage.getItem('savedLogin');
        if (savedLogin) {
            setLoginInput(savedLogin);
            setRememberMe(true);
        }
    }, []);

    const handleLogin = async () => {
        if (!loginInput || !passwordInput) {
            setLoginMessage({ text: 'لطفاً همه فیلدها را پر کنید.', color: 'crimson' });
            return;
        }

        setLoginMessage({ text: 'در حال ورود...', color: '#1565c0' });

        try {
            const response = await fetch('http://localhost:5000/api/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ login: loginInput, password: passwordInput }),
            });

            const data = await response.json();

            if (response.ok) {
                setLoginMessage({ text: 'ورود موفقیت‌آمیز بود!', color: 'green' });
                if (rememberMe) {
                    localStorage.setItem('savedLogin', loginInput);
                } else {
                    localStorage.removeItem('savedLogin');
                }
                localStorage.setItem('loggedInUser', data.user);
                history.push('/dashboard'); // <-- تغییر اصلی اینجاست
            } else {
                setLoginMessage({ text: data.message || 'اطلاعات نادرست است.', color: 'crimson' });
            }
        } catch (error) {
            setLoginMessage({ text: 'خطا در ارتباط با سرور.', color: 'crimson' });
        }
    };

    const handleSignup = async () => {
        if (!loginInput || !passwordInput) {
            setLoginMessage({ text: 'لطفاً فیلدها را پر کنید.', color: 'crimson' });
            return;
        }
        try {
            const response = await fetch('http://localhost:5000/api/signup', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ login: loginInput, password: passwordInput }),
            });
            const data = await response.json();
            if (response.ok) {
                setLoginMessage({ text: 'ثبت‌نام موفقیت‌آمیز بود!', color: 'green' });
            } else {
                setLoginMessage({ text: data.message || 'خطا در ثبت‌نام.', color: 'crimson' });
            }
        } catch (error) {
            setLoginMessage({ text: 'خطا در ارتباط با سرور.', color: 'crimson' });
        }
    };

    const handleGuestLogin = () => {
        localStorage.setItem('loggedInUser', 'guest');
        history.push('/dashboard');
    };

    const toggleChat = () => setChatOpen(!isChatOpen);

    const handleSendChat = () => {
        if (chatInput.trim() === '') return;
        const newMessages = [...chatMessages, { from: 'user', text: chatInput }];
        setChatMessages(newMessages);
        setChatInput('');
        setTimeout(() => {
            setChatMessages(prev => [...prev, { from: 'bot', text: 'پشتیبان به زودی پاسخ می‌دهد.' }]);
        }, 1000);
    };

    return (
        <>
            <div className="navbar">
                <div><strong>👶 رشدیار</strong></div>
                <div>
                    <a href="#home">خانه</a>
                    <a href="#about">درباره ما</a>
                    <a href="#contact">تماس با ما</a>
                </div>
            </div>
            <div className="login-container">
                <h2>ورود به حساب</h2>
                <input type="text" value={loginInput} onChange={e => setLoginInput(e.target.value)} placeholder="ایمیل یا شماره موبایل" />
                <input type="password" value={passwordInput} onChange={e => setPasswordInput(e.target.value)} placeholder="رمز عبور" />
                <div className="form-footer">
                    <label>
                        <input type="checkbox" checked={rememberMe} onChange={e => setRememberMe(e.target.checked)} /> مرا به خاطر بسپار
                    </label>
                    <a href="#forgot">فراموشی رمز؟</a>
                </div>
                <button onClick={handleLogin}>ورود</button>
                <button onClick={handleSignup} style={{ backgroundColor: '#4CAF50', marginTop: '10px' }}>ثبت‌نام</button>
                <button onClick={handleGuestLogin} style={{ backgroundColor: '#78909c', marginTop: '10px' }}>ورود مهمان</button>
                {loginMessage && <div className="message" style={{ color: loginMessage.color }}>{loginMessage.text}</div>}
            </div>
            {/* Chat components... */}
        </>
    );
};

export default LoginPage;
