import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
    const history = useHistory();
    const [loginInput, setLoginInput] = useState('');
    const [passwordInput, setPasswordInput] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [loginMessage, setLoginMessage] = useState('');
    const [isChatOpen, setChatOpen] = useState(false);
    const [chatInput, setChatInput] = useState('');
    const [chatMessages, setChatMessages] = useState([
        { from: 'bot', text: 'ÿ≥ŸÑÿßŸÖ! ⁄Üÿ∑Ÿàÿ± ŸÖ€å‚Äåÿ™ŸàŸÜŸÖ ⁄©ŸÖ⁄©‚Äåÿ™ŸàŸÜ ⁄©ŸÜŸÖÿü' }
    ]);

    useEffect(() => {
        const savedLogin = localStorage.getItem('savedLogin');
        if (savedLogin) {
            setLoginInput(savedLogin);
            setRememberMe(true);
        }
    }, []);

    const handleLogin = async () => {
        if (!loginInput || !passwordInput) {
            setLoginMessage({ text: 'ŸÑÿ∑ŸÅÿßŸã ŸáŸÖŸá ŸÅ€åŸÑÿØŸáÿß ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ.', color: 'crimson' });
            return;
        }

        setLoginMessage({ text: 'ÿØÿ± ÿ≠ÿßŸÑ Ÿàÿ±ŸàÿØ...', color: '#1565c0' });

        try {
            const response = await fetch('http://localhost:5000/api/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ login: loginInput, password: passwordInput }),
            });

            const data = await response.json();

            if (response.ok) {
                setLoginMessage({ text: 'Ÿàÿ±ŸàÿØ ŸÖŸàŸÅŸÇ€åÿ™‚Äåÿ¢ŸÖ€åÿ≤ ÿ®ŸàÿØ!', color: 'green' });
                if (rememberMe) {
                    localStorage.setItem('savedLogin', loginInput);
                } else {
                    localStorage.removeItem('savedLogin');
                }
                localStorage.setItem('loggedInUser', data.user);
                history.push('/dashboard'); // <-- ÿ™ÿ∫€å€åÿ± ÿßÿµŸÑ€å ÿß€åŸÜÿ¨ÿßÿ≥ÿ™
            } else {
                setLoginMessage({ text: data.message || 'ÿßÿ∑ŸÑÿßÿπÿßÿ™ ŸÜÿßÿØÿ±ÿ≥ÿ™ ÿßÿ≥ÿ™.', color: 'crimson' });
            }
        } catch (error) {
            setLoginMessage({ text: 'ÿÆÿ∑ÿß ÿØÿ± ÿßÿ±ÿ™ÿ®ÿßÿ∑ ÿ®ÿß ÿ≥ÿ±Ÿàÿ±.', color: 'crimson' });
        }
    };

    const handleSignup = async () => {
        if (!loginInput || !passwordInput) {
            setLoginMessage({ text: 'ŸÑÿ∑ŸÅÿßŸã ŸÅ€åŸÑÿØŸáÿß ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ.', color: 'crimson' });
            return;
        }
        try {
            const response = await fetch('http://localhost:5000/api/signup', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ login: loginInput, password: passwordInput }),
            });
            const data = await response.json();
            if (response.ok) {
                setLoginMessage({ text: 'ÿ´ÿ®ÿ™‚ÄåŸÜÿßŸÖ ŸÖŸàŸÅŸÇ€åÿ™‚Äåÿ¢ŸÖ€åÿ≤ ÿ®ŸàÿØ!', color: 'green' });
            } else {
                setLoginMessage({ text: data.message || 'ÿÆÿ∑ÿß ÿØÿ± ÿ´ÿ®ÿ™‚ÄåŸÜÿßŸÖ.', color: 'crimson' });
            }
        } catch (error) {
            setLoginMessage({ text: 'ÿÆÿ∑ÿß ÿØÿ± ÿßÿ±ÿ™ÿ®ÿßÿ∑ ÿ®ÿß ÿ≥ÿ±Ÿàÿ±.', color: 'crimson' });
        }
    };

    const handleGuestLogin = () => {
        localStorage.setItem('loggedInUser', 'guest');
        history.push('/dashboard');
    };

    const toggleChat = () => setChatOpen(!isChatOpen);

    const handleSendChat = () => {
        if (chatInput.trim() === '') return;
        const newMessages = [...chatMessages, { from: 'user', text: chatInput }];
        setChatMessages(newMessages);
        setChatInput('');
        setTimeout(() => {
            setChatMessages(prev => [...prev, { from: 'bot', text: 'Ÿæÿ¥ÿ™€åÿ®ÿßŸÜ ÿ®Ÿá ÿ≤ŸàÿØ€å Ÿæÿßÿ≥ÿÆ ŸÖ€å‚ÄåÿØŸáÿØ.' }]);
        }, 1000);
    };

    return (
        <>
            <div className="navbar">
                <div><strong>üë∂ ÿ±ÿ¥ÿØ€åÿßÿ±</strong></div>
                <div>
                    <a href="#home">ÿÆÿßŸÜŸá</a>
                    <a href="#about">ÿØÿ±ÿ®ÿßÿ±Ÿá ŸÖÿß</a>
                    <a href="#contact">ÿ™ŸÖÿßÿ≥ ÿ®ÿß ŸÖÿß</a>
                </div>
            </div>
            <div className="login-container">
                <h2>Ÿàÿ±ŸàÿØ ÿ®Ÿá ÿ≠ÿ≥ÿßÿ®</h2>
                <input type="text" value={loginInput} onChange={e => setLoginInput(e.target.value)} placeholder="ÿß€åŸÖ€åŸÑ €åÿß ÿ¥ŸÖÿßÿ±Ÿá ŸÖŸàÿ®ÿß€åŸÑ" />
                <input type="password" value={passwordInput} onChange={e => setPasswordInput(e.target.value)} placeholder="ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ±" />
                <div className="form-footer">
                    <label>
                        <input type="checkbox" checked={rememberMe} onChange={e => setRememberMe(e.target.checked)} /> ŸÖÿ±ÿß ÿ®Ÿá ÿÆÿßÿ∑ÿ± ÿ®ÿ≥Ÿæÿßÿ±
                    </label>
                    <a href="#forgot">ŸÅÿ±ÿßŸÖŸàÿ¥€å ÿ±ŸÖÿ≤ÿü</a>
                </div>
                <button onClick={handleLogin}>Ÿàÿ±ŸàÿØ</button>
                <button onClick={handleSignup} style={{ backgroundColor: '#4CAF50', marginTop: '10px' }}>ÿ´ÿ®ÿ™‚ÄåŸÜÿßŸÖ</button>
                <button onClick={handleGuestLogin} style={{ backgroundColor: '#78909c', marginTop: '10px' }}>Ÿàÿ±ŸàÿØ ŸÖŸáŸÖÿßŸÜ</button>
                {loginMessage && <div className="message" style={{ color: loginMessage.color }}>{loginMessage.text}</div>}
            </div>
            {/* Chat components... */}
        </>
    );
};

export default LoginPage;
