import React from 'react';
import { useHistory } from 'react-router-dom';
import './MyChildrenPage.css';

const mockChildren = [
    { id: 1, name: 'آوا محمدی', age: '۵ ساله', avatar: 'https://via.placeholder.com/80' },
    { id: 2, name: 'آرین محمدی', age: '۲ ساله', avatar: 'https://via.placeholder.com/80' },
];

const MyChildrenPage = () => {
    const history = useHistory();

    return (
        <div className="children-page">
            <nav className="page-nav">
                <button onClick={() => history.push('/dashboard')} className="back-btn">
                    &rarr; بازگشت
                </button>
                <h1>کودکان من</h1>
            </nav>
            <div className="children-content">
                <button className="add-child-btn">+ افزودن کودک جدید</button>
                <div className="children-list">
                    {mockChildren.map(child => (
                        <div key={child.id} className="child-card">
                            <img src={child.avatar} alt={child.name} className="child-avatar" />
                            <div className="child-info">
                                <h3>{child.name}</h3>
                                <p>{child.age}</p>
                            </div>
                            <button className="view-profile-btn">مشاهده پرونده</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default MyChildrenPage;
