import React from 'react';
import { useHistory } from 'react-router-dom';
import './MyChildrenPage.css';

const mockChildren = [
    { id: 1, name: 'آوا محمدی', age: '۵ ساله', avatar: 'https://i.pravatar.cc/100?u=a' },
    { id: 2, name: 'آرین محمدی', age: '۲ ساله', avatar: 'https://i.pravatar.cc/100?u=b' },
];

const MyChildrenPage = () => {
    const history = useHistory();

    return (
        <div className="children-page-final">
            <nav className="page-nav-final">
                <div className="nav-placeholder"></div> {/* Empty div for spacing */}
                <h1>کودکان من</h1>
                <button onClick={() => history.push('/dashboard')} className="home-btn-final">&#127968;</button> {/* Home Icon */}
            </nav>
            <div className="children-content-final">
                <button className="add-child-btn-final">+ افزودن کودک جدید</button>
                <div className="children-list-final">
                    {mockChildren.map(child => (
                        <div key={child.id} className="child-card-final">
                            <img src={child.avatar} alt={child.name} className="child-avatar-final" />
                            <div className="child-info-final">
                                <h3>{child.name}</h3>
                                <p>{child.age}</p>
                            </div>
                            <button className="view-profile-btn-final">مشاهده</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default MyChildrenPage;
